    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if("".equals(rol.getText()))
        {
            JOptionPane.showMessageDialog(this, "Please fill up all details.");
        }
        else
        {
            ExamType=(String) Exam.getSelectedItem();
            Class=(String) cls.getSelectedItem();
            roll=Integer.parseInt(rol.getText());
            Sect=(String) section.getSelectedItem();
            Result obj=new Result();
            obj.setVisible(true);
            this.setVisible(false);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

